import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
  const [form, setForm] = useState({ email: '', password: '' });

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', form);
      const token = res.data.token;  // The JWT token from the backend
      const user = res.data.user;    // User data from the backend
  
      // Store the token and user information in localStorage
      localStorage.setItem('authToken', token);
      localStorage.setItem('user', JSON.stringify(user));
      const userr = JSON(localStorage.getItem('au'));

      if (userr) {
        // Now you can safely access user.role
        console.log('User role:', userr.role);
      } else {
        console.log('User not found in localStorage');
      }

      // Alert for debugging
      alert(`Token: ${token}\nUser Info: ${JSON.stringify(user, null, 2)}`);
  
      // Redirect based on user role
      if (userr.role === 'admin') {
        window.location.href = '/admin-dashboard';
      } else if (userr.role === 'tutor') {
        window.location.href = '/tutor-dashboard';
      } else if (userr.role === 'student') {
        window.location.href = '/student-dashboard';
      }
    } catch (err) {
      alert(err.response.data.error || 'Error');
    }
  };
  
  return (
    <form onSubmit={handleSubmit}>
      <h2>Login</h2>
      <input name="email" placeholder="Email" onChange={handleChange} />
      <input name="password" type="password" placeholder="Password" onChange={handleChange} />
      <button type="submit">Log In</button>
    </form>
  );
};

export default Login;
