"use client"

import { useEffect, useState } from "react"
import axios from "axios"
import Layout from "../components/layout"
import { ChevronDown, ChevronUp, UserPlus, UserMinus } from "lucide-react"

const CourseList = () => {
  const [courses, setCourses] = useState([])
  const [students, setStudents] = useState([])
  const [loading, setLoading] = useState(true)
  const [enrollSelection, setEnrollSelection] = useState({})
  const [removeSelection, setRemoveSelection] = useState({})
  const [expandedCourses, setExpandedCourses] = useState({})

  useEffect(() => {
    const fetchData = async () => {
      try {
        const courseRes = await axios.get("/api/courses/course-list")
        const studentRes = await axios.get("/api/users?role=student")
        setCourses(courseRes.data)
        setStudents(studentRes.data)
      } catch (err) {
        console.error("Failed to fetch data", err)
      } finally {
        setLoading(false)
      }
    }

    fetchData()
  }, [])

  const handleEnroll = async (courseId, studentId) => {
    if (!studentId) {
      alert("Please select a student to enroll.")
      return
    }

    const confirm = window.confirm("Are you sure you want to enroll this student in the course?")
    if (!confirm) return

    try {
      await axios.post(`/api/courses/${courseId}/enroll`, { studentId })
      alert("Student successfully enrolled!")
      // Refresh data
      const courseRes = await axios.get("/api/courses/course-list")
      setCourses(courseRes.data)
    } catch (err) {
      console.error("Enrollment failed:", err.response?.data || err.message)
      alert("Failed to enroll student. Please try again.")
    }
  }

  const handleRemove = async (courseId) => {
    const studentId = removeSelection[courseId]
    if (!studentId) {
      alert("Please select a student to remove.")
      return
    }

    const confirm = window.confirm("Are you sure you want to remove this student from the course?")
    if (!confirm) return

    try {
      await axios.post(`/api/courses/${courseId}/remove-student`, { studentId })
      alert("Student successfully removed!")
      // Refresh data
      const courseRes = await axios.get("/api/courses/course-list")
      setCourses(courseRes.data)
    } catch (err) {
      console.error("Failed to remove student", err)
      alert("Failed to remove student. Please try again.")
    }
  }

  const toggleCourseExpand = (courseId) => {
    setExpandedCourses((prev) => ({
      ...prev,
      [courseId]: !prev[courseId],
    }))
  }

  if (loading)
    return (
      <Layout>
        <div className="flex justify-center items-center h-64">
          <div className="animate-pulse text-blue-600">Loading courses...</div>
        </div>
      </Layout>
    )

  return (
    <Layout>
      <div className="space-y-6">
        <div>
          <h1 className="text-2xl font-semibold text-gray-900">Course Management</h1>
          <p className="mt-1 text-sm text-gray-500">View and manage all courses and student enrollments.</p>
        </div>

        {courses.length === 0 ? (
          <div className="bg-white shadow overflow-hidden sm:rounded-md p-6 text-center text-gray-500">
            No courses found.
          </div>
        ) : (
          <div className="bg-white shadow overflow-hidden sm:rounded-md">
            <ul className="divide-y divide-gray-200">
              {courses.map((course) => (
                <li key={course._id}>
                  <div className="px-4 py-4 sm:px-6">
                    <div className="flex items-center justify-between">
                      <div className="flex flex-col">
                        <h3 className="text-lg font-medium text-gray-900">{course.title}</h3>
                        <p className="mt-1 text-sm text-gray-500">{course.description}</p>
                        <p className="mt-2 text-sm text-gray-600">
                          Instructor: {course.instructorId?.name} ({course.instructorId?.email})
                        </p>
                      </div>
                      <button
                        onClick={() => toggleCourseExpand(course._id)}
                        className="ml-2 bg-white rounded-full p-1 text-gray-400 hover:text-gray-500 focus:outline-none"
                      >
                        {expandedCourses[course._id] ? (
                          <ChevronUp className="h-5 w-5" />
                        ) : (
                          <ChevronDown className="h-5 w-5" />
                        )}
                      </button>
                    </div>

                    {expandedCourses[course._id] && (
                      <div className="mt-4 border-t border-gray-100 pt-4">
                        <div className="mb-4">
                          <h4 className="text-sm font-medium text-gray-900 mb-2">Enrolled Students</h4>
                          {course.studentsEnrolled && course.studentsEnrolled.length > 0 ? (
                            <ul className="divide-y divide-gray-200 border border-gray-200 rounded-md">
                              {course.studentsEnrolled.map((s) => (
                                <li
                                  key={s._id}
                                  className="px-3 py-2 text-sm text-gray-700 flex justify-between items-center"
                                >
                                  <span>
                                    {s.name} ({s.email})
                                  </span>
                                </li>
                              ))}
                            </ul>
                          ) : (
                            <p className="text-sm text-gray-500">No students enrolled yet.</p>
                          )}
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                          {/* Enroll Form */}
                          <div className="space-y-2">
                            <h4 className="text-sm font-medium text-gray-900">Enroll Student</h4>
                            <div className="flex space-x-2">
                              <select
                                value={enrollSelection[course._id] || ""}
                                onChange={(e) =>
                                  setEnrollSelection({ ...enrollSelection, [course._id]: e.target.value })
                                }
                                className="block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm"
                              >
                                <option value="">Select student to enroll</option>
                                {students.map((s) => (
                                  <option key={s._id} value={s._id}>
                                    {s.name} ({s.email})
                                  </option>
                                ))}
                              </select>
                              <button
                                onClick={() => handleEnroll(course._id, enrollSelection[course._id])}
                                className="inline-flex items-center px-3 py-2 border border-transparent text-sm leading-4 font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                              >
                                <UserPlus className="h-4 w-4 mr-1" />
                                Enroll
                              </button>
                            </div>
                          </div>

                          {/* Remove Form */}
                          <div className="space-y-2">
                            <h4 className="text-sm font-medium text-gray-900">Remove Student</h4>
                            <div className="flex space-x-2">
                              <select
                                value={removeSelection[course._id] || ""}
                                onChange={(e) =>
                                  setRemoveSelection({ ...removeSelection, [course._id]: e.target.value })
                                }
                                className="block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm"
                                disabled={!course.studentsEnrolled || course.studentsEnrolled.length === 0}
                              >
                                <option value="">Select student to remove</option>
                                {course.studentsEnrolled &&
                                  course.studentsEnrolled.map((s) => (
                                    <option key={s._id} value={s._id}>
                                      {s.name} ({s.email})
                                    </option>
                                  ))}
                              </select>
                              <button
                                onClick={() => handleRemove(course._id)}
                                className="inline-flex items-center px-3 py-2 border border-transparent text-sm leading-4 font-medium rounded-md text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
                                disabled={!course.studentsEnrolled || course.studentsEnrolled.length === 0}
                              >
                                <UserMinus className="h-4 w-4 mr-1" />
                                Remove
                              </button>
                            </div>
                          </div>
                        </div>
                      </div>
                    )}
                  </div>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </Layout>
  )
}

export default CourseList
