// src/components/Dashboard/StudentDashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios'; // Make sure axios is installed
import './StudentDashboard.css';

const StudentDashboard = () => {
  const [courses, setCourses] = useState([]);
  const [assignments, setAssignments] = useState([]);
  const [deadlines, setDeadlines] = useState([]);
  const [feedbacks, setFeedbacks] = useState([]);

  useEffect(() => {
    // Fetch enrolled courses
    axios.get('/api/courses/my-courses', { withCredentials: true })
      .then(response => {
        setCourses(response.data); // Set the list of enrolled courses
      })
      .catch(error => {
        console.error("Error fetching courses:", error);
      });

    // Fetch completed assignments (you might need to adjust this route)
    // You would likely need an API route for completed assignments
    axios.get('/api/assignments/completed', { withCredentials: true })
      .then(response => {
        setAssignments(response.data);
      })
      .catch(error => {
        console.error("Error fetching assignments:", error);
      });

    // Fetch upcoming deadlines (you might need to adjust this route as well)
    // An API endpoint to fetch deadlines is needed
    axios.get('/api/assignments/upcoming-deadlines', { withCredentials: true })
      .then(response => {
        setDeadlines(response.data);
      })
      .catch(error => {
        console.error("Error fetching deadlines:", error);
      });

    // Fetch pending feedback (you might need an API route for this too)
    axios.get('/api/assignments/pending-feedback', { withCredentials: true })
      .then(response => {
        setFeedbacks(response.data);
      })
      .catch(error => {
        console.error("Error fetching feedbacks:", error);
      });
  }, []);

  return (
    <div className="student-dashboard">
      <h1>Student Dashboard</h1>
      <div className="dashboard-content">
        <div className="card">
          <h3>Enrolled Courses</h3>
          <p>{courses.length}</p>
        </div>
        <div className="card">
          <h3>Completed Assignments</h3>
          <p>{assignments.length}</p>
        </div>
        <div className="card">
          <h3>Upcoming Deadlines</h3>
          <p>{deadlines.length}</p>
        </div>
        <div className="card">
          <h3>Pending Feedback</h3>
          <p>{feedbacks.length}</p>
        </div>
      </div>
    </div>
  );
};

export default StudentDashboard;
