import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TutorCourseView = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTeachingCourses = async () => {
      try {
        const authToken = localStorage.getItem('authToken');
        console.log("Using auth token:", authToken); // Check the token

        const res = await axios.get('http://localhost:5000/api/courses/tutor-course-view', {
          headers: { Authorization: `Bearer ${authToken}` },
        });

        setCourses(res.data); // Set courses data from the response
        setLoading(false); // Set loading to false after fetching courses
      } catch (err) {
        console.error('Failed to fetch tutor courses:', err.response ? err.response.data : err.message);
        setError('Failed to load your courses.');
        setLoading(false); // Stop loading on error
      }
    };

    fetchTeachingCourses();
  }, []);

  if (loading) return <p>Loading your teaching courses...</p>;
  if (error) return <p className="text-red-500">{error}</p>;

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">My Teaching Courses</h2>
      {courses.length === 0 ? (
        <p>You are not teaching any courses yet.</p>
      ) : (
        <ul className="space-y-4">
          {courses.map((course) => (
            <li key={course._id} className="p-4 border rounded-lg shadow-sm">
              <h3 className="text-lg font-semibold">{course.title}</h3>
              <p>{course.description}</p>
              <p className="text-sm text-gray-600 mt-1">
                Instructor: {course.instructorId?.name} ({course.instructorId?.email})
              </p>
              <p className="text-sm text-blue-600 mt-1">
                Enrolled Students: {course.studentCount}
              </p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TutorCourseView;
