// src/App.js
import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from "react-router-dom";

import LoginPage from "./components/LoginPage";
import SignupPage from "./components/SignupPage";

import TutorUploadAssignment from "./components/TutorUploadAssignment";
import StudentCourseView from "./components/StudentCourseView";
import StudentSubmitAssignment from "./components/StudentSubmitAssignment";
import StudentProgress from "./components/StudentProgress";
import TutorViewProgress from "./components/TutorViewProgress";
import CourseList from "./pages/Courses/CourseList";
import CreateCourse from "./pages/Courses/CreateCourse";

import InstructorReviews from "./components/InstructorReviews";
import SubmitReviewForm from "./components/SubmitReviewForm";
import CreateTutor from "./pages/CreateTutor";
import CreateStudent from "./pages/CreateStudent";

import AuthPage from "./pages/AuthPage"; 
import AdminDashboard from "./pages/AdminDashboard";
import TutorDashboard from "./pages/TutorDashboard";
import StudentDashboard from "./pages/StudentDashboard";

import { ChatProvider } from "../src/ChatContext";
import ChatLayout from "../src/components/Chat/ChatLayout";
import "./App.css";

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [role, setRole] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");
    const storedRole = localStorage.getItem("role");
    if (token && storedRole) {
      setIsLoggedIn(true);
      setRole(storedRole);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    localStorage.removeItem("name");
    setIsLoggedIn(false);
    setRole("");
  };

  return (
    <Router>
      <div style={{ padding: "20px" }}>
        {/* 🔗 Navigation */}
        <nav style={{ marginBottom: "30px" }}>
          <Link to="/">
            <button>🏠 Home</button>
          </Link>

          {isLoggedIn && (
            <>
              {role === "admin" && (
                <>
                  <Link to="/create-course">
                    <button>🆕 Create Course</button>
                  </Link>
                  <Link to="/create-tutor">
                    <button>👨‍🏫 Create Tutor</button>
                  </Link>
                  <Link to="/create-student">
                    <button>👨‍🎓 Create Student</button>
                  </Link>
                  <Link to="/admin/dashboard">
                    <button>📊 Admin Dashboard</button>
                  </Link>
                </>
              )}
              {role === "tutor" && (
                <>
                  <Link to="/tutor/dashboard">
                    <button>📋 Tutor Dashboard</button>
                  </Link>
                  <Link to="/tutor-upload">
                    <button>📤 Upload Assignment</button>
                  </Link>
                  <Link to="/tutor-progress">
                    <button>📈 View Student Progress</button>
                  </Link>
                </>
              )}
              {role === "student" && (
                <>
                  <Link to="/student/dashboard">
                    <button>👨‍🎓 Student Dashboard</button>
                  </Link>
                  <Link to="/student-course-view">
                    <button>📚 View Courses</button>
                  </Link>
                  <Link to="/student-submit">
                    <button>📨 Submit Assignment</button>
                  </Link>
                  <Link to="/student-progress">
                    <button>📈 My Progress</button>
                  </Link>
                </>
              )}
              <Link to="/courses">
                <button>📚 Course List</button>
              </Link>
              <Link to="/instructor-reviews">
                <button>⭐ Reviews</button>
              </Link>
              <Link to="/submit-review">
                <button>✏️ Submit Review</button>
              </Link>
              <Link to="/chat">
                <button>💬 Chat</button>
              </Link>
              <button onClick={handleLogout}>🚪 Logout</button>
            </>
          )}

          {!isLoggedIn && (
            <Link to="/">
              <button>🔐 Login</button>
            </Link>
          )}
        </nav>

        {/* 📌 Route Setup */}
        <Routes>
          <Route path="/" element={<AuthPage setIsLoggedIn={setIsLoggedIn} setRole={setRole} />} />

          <Route path="/admin/dashboard" element={<AdminDashboard />} />
          <Route path="/tutor/dashboard" element={<TutorDashboard />} />
          <Route path="/student/dashboard" element={<StudentDashboard />} />

          <Route path="/tutor-upload" element={<TutorUploadAssignment />} />
          <Route path="/tutor-progress" element={<TutorViewProgress />} />

          <Route path="/student-course-view" element={<StudentCourseView />} />
          <Route path="/student-submit" element={<StudentSubmitAssignment />} />
          <Route path="/student-progress" element={<StudentProgress />} />

          <Route path="/courses" element={<CourseList />} />
          <Route path="/create-course" element={<CreateCourse />} />

          <Route path="/instructor-reviews" element={<InstructorReviews />} />
          <Route path="/instructor-reviews/:instructorId" element={<InstructorReviews />} />
          <Route path="/submit-review" element={<SubmitReviewForm />} />
          <Route path="/submit-review/:instructorId" element={<SubmitReviewForm />} />

          <Route path="/create-tutor" element={<CreateTutor />} />
          <Route path="/create-student" element={<CreateStudent />} />

          <Route path="/chat" element={
            <ChatProvider>
              <ChatLayout />
            </ChatProvider>
          } />
        </Routes>
      </div>
    </Router>
  );
};

const bcrypt = require('bcryptjs');
const hashedPassword = bcrypt.hashSync('admin123', 10); // 10 is the salt rounds
console.log(hashedPassword);

export default App;
