"use client"

import { useEffect, useState } from "react"
import axios from "axios"
import "./tutor-course-view.css"

const TutorCourseView = () => {
  const [courses, setCourses] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")

  useEffect(() => {
    const fetchTeachingCourses = async () => {
      try {
        const authToken = localStorage.getItem("authToken")
        console.log("Using auth token:", authToken) // Check the token

        const res = await axios.get("http://localhost:5000/api/courses/tutor-course-view", {
          headers: { Authorization: `Bearer ${authToken}` },
        })

        setCourses(res.data) // Set courses data from the response
        setLoading(false) // Set loading to false after fetching courses
      } catch (err) {
        console.error("Failed to fetch tutor courses:", err.response ? err.response.data : err.message)
        setError("Failed to load your courses.")
        setLoading(false) // Stop loading on error
      }
    }

    fetchTeachingCourses()
  }, [])

  if (loading) return <p className="loading-message">Loading your teaching courses...</p>
  if (error) return <p className="error-message">{error}</p>

  return (
    <div className="tutor-course-container">
      <h2 className="page-title">My Teaching Courses</h2>
      {courses.length === 0 ? (
        <p className="empty-message">You are not teaching any courses yet.</p>
      ) : (
        <ul className="course-list">
          {courses.map((course) => (
            <li key={course._id} className="course-item">
              <h3 className="course-title">{course.title}</h3>
              <p className="course-description">{course.description}</p>
              <p className="instructor-info">
                Instructor: {course.instructorId?.name} ({course.instructorId?.email})
              </p>
              <p className="student-count">Enrolled Students: {course.studentCount}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  )
}

export default TutorCourseView
