import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StudentCourseView = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Fetch student’s courses on component mount
  useEffect(() => {
    const fetchCourses = async () => {
      try {
        // Assuming the token is stored in localStorage
        const token = localStorage.getItem('authToken');
        const res = await axios.get('/api/courses/student-course-view', {
          headers: {
            Authorization: `Bearer ${token}`,  // Send the token for authorization
          },
        });

        setCourses(res.data);  // Set courses to state
      } catch (err) {
        console.error('❌ Failed to fetch student courses:', err);
        setError('Failed to load courses.');
      } finally {
        setLoading(false);
      }
    };

    fetchCourses();
  }, []);

  if (loading) return <p>Loading your courses...</p>;
  if (error) return <p className="text-red-500">{error}</p>;

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">My Enrolled Courses</h2>
      {courses.length === 0 ? (
        <p>You are not enrolled in any courses yet.</p>
      ) : (
        <ul className="space-y-4">
          {courses.map((course) => (
            <li key={course._id} className="p-4 border rounded-lg shadow-sm">
              <h3 className="text-lg font-semibold">{course.title}</h3>
              <p>{course.description}</p>
              <p className="text-sm text-gray-600 mt-1">
                Instructor: {course.instructorId?.name} ({course.instructorId?.email})
              </p>
              <p className="text-sm text-blue-600 mt-1">
                Enrolled Students: {course.studentsEnrolled?.length || 0}
              </p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default StudentCourseView;
