"use client"

import { useState, useEffect } from "react"
import axios from "axios"
import { useNavigate } from "react-router-dom"
import Layout from "../components/layout"
import { ArrowLeft } from "lucide-react"

const CreateCourse = () => {
  const [title, setTitle] = useState("")
  const [description, setDescription] = useState("")
  const [instructorId, setInstructorId] = useState("")
  const [instructors, setInstructors] = useState([])
  const [successMessage, setSuccessMessage] = useState("")
  const [loading, setLoading] = useState(false)
  const [fetchingInstructors, setFetchingInstructors] = useState(true)

  const navigate = useNavigate()

  useEffect(() => {
    const fetchInstructors = async () => {
      try {
        const res = await axios.get("/api/users?role=tutor")
        setInstructors(res.data)
      } catch (err) {
        console.error("Failed to fetch instructors:", err.response?.data || err.message)
      } finally {
        setFetchingInstructors(false)
      }
    }

    fetchInstructors()
  }, [])

  const handleSubmit = async (e) => {
    e.preventDefault()
    setLoading(true)

    try {
      const token = localStorage.getItem("authToken")
      await axios.post(
        "/api/courses",
        {
          title,
          description,
          instructorId,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        },
      )

      setSuccessMessage("Course created successfully!")
      setTitle("")
      setDescription("")
      setInstructorId("")

      // Redirect after a short delay
      setTimeout(() => {
        navigate("/course-list")
      }, 2000)
    } catch (err) {
      console.error("Failed to create course", err)
      setSuccessMessage("Failed to create course.")
    } finally {
      setLoading(false)
    }
  }

  return (
    <Layout>
      <div className="space-y-6">
        <div className="flex items-center">
          <button
            onClick={() => navigate(-1)}
            className="mr-4 inline-flex items-center px-3 py-2 border border-gray-300 shadow-sm text-sm leading-4 font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
          >
            <ArrowLeft className="h-4 w-4 mr-1" />
            Back
          </button>
          <h1 className="text-2xl font-semibold text-gray-900">Create Course</h1>
        </div>

        {successMessage && (
          <div className="rounded-md bg-green-50 p-4">
            <div className="flex">
              <div className="ml-3">
                <p className="text-sm font-medium text-green-800">{successMessage}</p>
              </div>
            </div>
          </div>
        )}

        <div className="bg-white shadow overflow-hidden sm:rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <label htmlFor="title" className="block text-sm font-medium text-gray-700">
                  Course Title
                </label>
                <input
                  type="text"
                  name="title"
                  id="title"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  required
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm"
                />
              </div>

              <div>
                <label htmlFor="description" className="block text-sm font-medium text-gray-700">
                  Course Description
                </label>
                <textarea
                  id="description"
                  name="description"
                  rows={4}
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  required
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm"
                />
              </div>

              <div>
                <label htmlFor="instructorId" className="block text-sm font-medium text-gray-700">
                  Assign Instructor
                </label>
                <select
                  id="instructorId"
                  name="instructorId"
                  value={instructorId}
                  onChange={(e) => setInstructorId(e.target.value)}
                  required
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm"
                  disabled={fetchingInstructors}
                >
                  <option value="">Select an instructor</option>
                  {instructors.map((inst) => (
                    <option key={inst._id} value={inst._id}>
                      {inst.name} ({inst.email})
                    </option>
                  ))}
                </select>
                {fetchingInstructors && <p className="mt-1 text-sm text-gray-500">Loading instructors...</p>}
              </div>

              <div>
                <button
                  type="submit"
                  disabled={loading}
                  className="inline-flex justify-center py-2 px-4 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {loading ? "Creating..." : "Create Course"}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </Layout>
  )
}

export default CreateCourse
