import { Link, useNavigate } from "react-router-dom";
import "./TutorDashboard.css";
import { useEffect, useState } from "react";

const TutorDashboard = () => {
  const navigate = useNavigate();
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch tutor's courses when component mounts
    const fetchTutorCourses = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          navigate("/login");
          return;
        }

        const response = await fetch("/api/courses/tutor", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          throw new Error("Failed to fetch courses");
        }

        const data = await response.json();
        setCourses(data);
      } catch (error) {
        console.error("Error fetching courses:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchTutorCourses();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  if (loading) {
    return <div className="loading-spinner">Loading...</div>;
  }

  return (
    <div className="tutor-dashboard">
      <div className="dashboard-container">
        <h2 className="dashboard-title">üë®‚Äçüè´ Tutor Dashboard</h2>

        <div className="dashboard-cards">
          <div className="dashboard-card">
            <h3 className="card-title">üìò My Teaching Courses</h3>
            <p className="card-description">
              {courses.length > 0
                ? `You're teaching ${courses.length} courses`
                : "You're not teaching any courses yet"}
            </p>
            <Link to="tutor-course-view" className="card-link">
              Go to Teaching Courses ‚Üí
            </Link>
          </div>

          <div className="dashboard-card">
            <h3 className="card-title">üìù Upload Assignments</h3>
            <p className="card-description">
              {courses.length > 0
                ? "Upload assignments for your courses"
                : "Create a course first to upload assignments"}
            </p>
            <Link 
              to="/upload-assignment" 
              className={`card-link ${courses.length === 0 ? "disabled-link" : ""}`}
            >
              Upload an Assignment ‚Üí
            </Link>
          </div>
        </div>

        <button onClick={handleLogout} className="logout-button">
          <span className="logout-icon">üö™</span> Logout
        </button>
      </div>
    </div>
  );
};

export default TutorDashboard;
