import React, { useEffect, useState } from "react";
import { Link, Redirect } from "react-router-dom";
import axios from "axios";
import './StudentDashboard.css'; // Import your CSS file

const StudentDashboard = ({ userData }) => {
  const [enrolledCourses, setEnrolledCourses] = useState([]);
  const [pendingAssignments, setPendingAssignments] = useState([]);
  const [feedbackCourses, setFeedbackCourses] = useState([]);
  const [chatCourses, setChatCourses] = useState([]);

  useEffect(() => {
    if (!userData || !userData.token) {
      // If userData or token is null, redirect to login page
      return <Redirect to="/" />;
    }

    const fetchEnrolledCourses = async () => {
      try {
        const response = await axios.get('/api/courses/my-courses', {
          headers: { Authorization: `Bearer ${userData.token}` }
        });
        setEnrolledCourses(response.data);
      } catch (error) {
        console.error("Error fetching enrolled courses:", error);
      }
    };

    const fetchAssignments = async () => {
      try {
        const response = await axios.get('/api/assignments/my-assignments', {
          headers: { Authorization: `Bearer ${userData.token}` }
        });
        setPendingAssignments(response.data.filter((assignment) => !assignment.submitted));
      } catch (error) {
        console.error("Error fetching assignments:", error);
      }
    };

    const fetchFeedbackCourses = async () => {
      try {
        setFeedbackCourses(enrolledCourses);
      } catch (error) {
        console.error("Error fetching feedback courses:", error);
      }
    };

    const fetchChatCourses = async () => {
      try {
        setChatCourses(enrolledCourses);
      } catch (error) {
        console.error("Error fetching chat courses:", error);
      }
    };

    fetchEnrolledCourses();
    fetchAssignments();
    fetchFeedbackCourses();
    fetchChatCourses();
  }, [userData]);

  if (!userData || !userData.token) {
    return <Redirect to="/" />; // Redirect if no user is authenticated
  }

  return (
    <div className="student-dashboard">
      <h1>Student Dashboard</h1>
      <div className="dashboard-content">
        <div className="card">
          <h3>Enrolled Courses</h3>
          {enrolledCourses.length > 0 ? (
            <ul>
              {enrolledCourses.map((course) => (
                <li key={course._id}>{course.name}</li>
              ))}
            </ul>
          ) : (
            <p>No enrolled courses</p>
          )}
        </div>

        <div className="card">
          <h3>Submit Assignment</h3>
          <select>
            <option value="">Select a course</option>
            {enrolledCourses.map((course) => (
              <option key={course._id} value={course._id}>
                {course.name}
              </option>
            ))}
          </select>
          <Link to="/student-submit">
            <button>Submit Assignment</button>
          </Link>
        </div>

        <div className="card">
          <h3>Give Feedback to Tutors</h3>
          {feedbackCourses.length > 0 ? (
            <select>
              <option value="">Select a course to give feedback</option>
              {feedbackCourses.map((course) => (
                <option key={course._id} value={course._id}>
                  {course.name}
                </option>
              ))}
            </select>
          ) : (
            <p>No courses available for feedback</p>
          )}
          <Link to="/submit-review">
            <button>Give Feedback</button>
          </Link>
        </div>

        <div className="card">
          <h3>Live Chat</h3>
          {chatCourses.length > 0 ? (
            <select>
              <option value="">Select a course to chat</option>
              {chatCourses.map((course) => (
                <option key={course._id} value={course._id}>
                  {course.name}
                </option>
              ))}
            </select>
          ) : (
            <p>No courses available for chat</p>
          )}
          <Link to="/chat">
            <button>Start Chat</button>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default StudentDashboard;
