"use client"

import { useState, useEffect } from "react"
import axios from "axios"
import { useNavigate } from "react-router-dom"
import "./CreateCourse.css"

const CreateCourse = () => {
  const [title, setTitle] = useState("")
  const [description, setDescription] = useState("")
  const [instructorId, setInstructorId] = useState("")
  const [instructors, setInstructors] = useState([])
  const [successMessage, setSuccessMessage] = useState("")

  useEffect(() => {
    const fetchInstructors = async () => {
      console.log("📡 Fetching instructors...")
      try {
        const res = await axios.get("/api/users?role=tutor")
        console.log("✅ Instructors fetched:", res.data)
        setInstructors(res.data)
      } catch (err) {
        console.error("❌ Failed to fetch instructors:", err.response?.data || err.message)
      }
    }

    fetchInstructors()
  }, [])

  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      await axios.post("/api/courses", {
        title,
        description,
        instructorId,
      })

      setTitle("")
      setDescription("")
      setInstructorId("")
      setSuccessMessage("Course created successfully!")
    } catch (err) {
      console.error("Failed to create course", err)
      setSuccessMessage("Failed to create course.")
    }
  }

  const navigate = useNavigate()

  return (
    <div className="create-course-container">
      <button onClick={() => navigate(-1)} className="back-button">
        🔙 Back
      </button>
      <div className="form-container">
        <h2 className="page-title">Create a New Course</h2>

        {successMessage && <p className="success-message">{successMessage}</p>}

        <form onSubmit={handleSubmit} className="course-form">
          <div className="form-group">
            <label className="form-label">Title</label>
            <input
              type="text"
              className="form-input"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">Description</label>
            <textarea
              className="form-textarea"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">Assign Instructor</label>
            <select
              className="form-select"
              value={instructorId}
              onChange={(e) => setInstructorId(e.target.value)}
              required
            >
              <option value="">Select an instructor</option>
              {instructors.map((inst) => (
                <option key={inst._id} value={inst._id}>
                  {inst.name} ({inst.email})
                </option>
              ))}
            </select>
          </div>

          <button type="submit" className="submit-button">
            Create Course
          </button>
        </form>
      </div>
    </div>
  )
}

export default CreateCourse
