"use client"

import { useEffect, useState } from "react"
import axios from "axios"
import "./CourseList.css"

const CourseList = () => {
  const [courses, setCourses] = useState([])
  const [loading, setLoading] = useState(true)
  const [enrollInputs, setEnrollInputs] = useState({}) // Track input for each course

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const res = await axios.get("/api/courses/course-list")
        setCourses(res.data)
      } catch (err) {
        console.error("Failed to fetch courses", err)
      } finally {
        setLoading(false)
      }
    }

    fetchCourses()
  }, [])

  const handleEnroll = async (courseId) => {
    const studentId = enrollInputs[courseId]
    if (!studentId) return

    try {
      await axios.post(`/api/courses/${courseId}/enroll`, { studentId })
      setEnrollInputs((prev) => ({ ...prev, [courseId]: "" }))
      // Refresh course list
      const res = await axios.get("/api/courses/course-list")
      setCourses(res.data)
    } catch (err) {
      console.error("Error enrolling student:", err)
    }
  }

  const handleRemove = async (courseId, studentId) => {
    try {
      await axios.post(`/api/courses/${courseId}/remove-student`, { studentId })
      const res = await axios.get("/api/courses/course-list")
      setCourses(res.data)
    } catch (err) {
      console.error("Error removing student:", err)
    }
  }

  if (loading) return <p className="loading-message">Loading courses...</p>

  return (
    <div className="course-list-container">
      <h2 className="page-title">Available Courses</h2>
      {courses.length === 0 ? (
        <p className="empty-message">No courses found.</p>
      ) : (
        <ul className="course-list">
          {courses.map((course) => (
            <li key={course._id} className="course-item">
              <h3 className="course-title">{course.title}</h3>
              <p className="course-description">{course.description}</p>
              <p className="instructor-info">
                Instructor: {course.instructorId?.name} ({course.instructorId?.email})
              </p>

              <div className="student-enrollment">
                <h4>Enrolled Students:</h4>
                <ul>
                  {course.studentsEnrolled?.length > 0 ? course.studentsEnrolled.map((student) => (
                    <li key={student._id}>
                      {student.name} ({student.email})
                      <button
                        className="remove-btn"
                        onClick={() => handleRemove(course._id, student._id)}
                      >
                        Remove
                      </button>
                    </li>
                  )) : <li>No students enrolled yet.</li>}
                </ul>

                <div className="enroll-form">
                  <input
                    type="text"
                    placeholder="Enter student ID"
                    value={enrollInputs[course._id] || ""}
                    onChange={(e) =>
                      setEnrollInputs((prev) => ({ ...prev, [course._id]: e.target.value }))
                    }
                  />
                  <button onClick={() => handleEnroll(course._id)}>Enroll</button>
                </div>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  )
}

export default CourseList
