    //   localStorage.removeItem("token")
    //   navigate("/")
    // }
  return (
    <div className="tutor-dashboard">
      <div className="dashboard-container">
        <h2 className="dashboard-title">üë®‚Äçüè´ Tutor Dashboard</h2>

        <div className="dashboard-cards">
          <div className="dashboard-card">
            <h3 className="card-title">üìò My Teaching Courses</h3>
            <p className="card-description">View all the courses you're assigned to teach.</p>
            <Link to="courses/tutor-course-view" className="card-link">
              Go to Teaching Courses ‚Üí
            </Link>
          </div>

          <div className="dashboard-card">
            <h3 className="card-title">üìù Upload Assignments</h3>
            <p className="card-description">Upload new assignments for your courses.</p>
            <Link to="/courses/upload-assignment" className="card-link">
              Upload an Assignment ‚Üí
            </Link>
          </div>
        </div>
      </div>
    </div>
  )
}

export default TutorDashboard
